int main(){
    int n; scanf("%d", &n);
    for(int i = 1; i <= n; i++) dp[i][i] = 1;
    for(int i = 1; i <= n; i++){
        for(int j = 1; j < i; j++)
            for(int k = 1; k < j && j + k <= i; k++)
