signed main()
{
	siz[0] = 1;
	int indax = 1;
	while (indax <= 20)
	{
		siz[indax] = get_siz(indax);
		indax ++;
	}
	while (true)
	{
		n = read();
		if (n == 0) break;
		int at = 1;
		while (n > siz[at])
		{
			n -= siz[at];
			at ++;
