var
    n      :longint;
    m      :array[0..51,0..51]of shortint; //0:empty -1:wall 1:itself
    bx,by  :longint;
    w      :array[1..200,1..2]of integer;
    open,cl:longint;
procedure init;
var
    i,j:longint;
begin
    fillchar(m,sizeof(m),0);
    for i:=0 to 51 do
    begin
        m[i,0]:=-1;
        m[i,51]:=-1;
        m[0,i]:=-1;
        m[51,i]:=-1;
    end;
    open:=0;
    cl:=0;
    for i:=11 to 30 do
    begin
        m[25,i]:=1;
        inc(cl);
        w[cl,1]:=25;
        w[cl,2]:=i;
    end;
    bx:=25;
    by:=30;
end;
procedure go(dir:char);
var
    i,j:longint;
begin
    case dir of
          'N':bx:=bx-1;
          'S':bx:=bx+1;
          'W':by:=by-1;
          'E':by:=by+1;
        end;
    m[bx,by]:=1;
    inc(cl);
    w[cl,1]:=bx;
    w[cl,2]:=by;
end;
procedure move;
var
    i,j:longint;
    step:longint;
    ok:boolean;
    dir:char;
begin
    step:=0;
    repeat
        inc(open);
        m[w[open,1],w[open,2]]:=0;
        ok:=true;
        inc(step);
        read(dir);
        case dir of
          'N':if m[bx-1,by]<>0 then begin ok:=false; break; end;
          'S':if m[bx+1,by]<>0 then begin ok:=false; break; end;
          'W':if m[bx,by-1]<>0 then begin ok:=false; break; end;
          'E':if m[bx,by+1]<>0 then begin ok:=false; break; end;
        end;
        go(dir);
    until (not ok) or (step=n);
    readln;
    if ok then
        writeln('The worm successfully made all ',n,' moves.')
    else
    begin
        case dir of
          'N':bx:=bx-1;
          'S':bx:=bx+1;
          'W':by:=by-1;
          'E':by:=by+1;
        end;
        if m[bx,by]=-1 then
            writeln('The worm ran off the board on move ',step,'.')
        else
            writeln('The worm ran into itself on move ',step,'.');
    end;
end;
begin
    repeat
        readln(n);
        if n=0 then break;
        init;
        move;
    until false;
end.

