```
#include<bits/stdc++.h>
using namespace std;

#define int long long
#define Size 1001000
#define ls index<<1
#define rs index<<1|1
int mon[4*Size];
#define L tree[index].l
#define R tree[index].r
#define Ad tree[index].add
#define S tree[index].sum

#define lL tree[ls].l
#define lR tree[ls].r
#define lAd tree[ls].add
#define lS tree[ls].sum

#define rL tree[rs].l
#define rR tree[rs].r
#define rAd tree[rs].add
#define rS tree[rs].sum
class SegmentTree
{
    public:
        struct node
        {
            int l,r;
            int add;
            int sum;
        }tree[Size];
        void Update(int index)
        {
            S=min(lS,rS);
        }
        void BuildTree(int l,int r,int index)
        {
            L=l,R=r,Ad=0;
            if (l==r)
            {
            	S=a[l];
                return;
            }
            int Mid=(l+r)>>1;
            BuildTree(l,Mid,ls);
            BuildTree(Mid+1,r,rs);
            Update(index);
        }
        void AddOne(int index,int Num)
        {
            Ad+=Num;
            S+=Num*(R-L+1);
            return;
        }
        void PushDown(int index)
        {
            if (Ad)
            {
                AddOne(ls,Ad);
                AddOne(rs,Ad);
                Ad=0;
                return;
            }
        }
        void Add(int l,int r,int Num,int index)
        {
            if (L>r or l>R) return;
            if (l<=L and R<=r)
            {
                AddOne(index,Num);
                return;
            }
            PushDown(index);
            Add(l,r,Num,ls);
            Add(l,r,Num,rs);
            Update(index);
        }
        int Query(int l,int r,int index)
        {
            if (L>r or l>R) return 0x3f3f3f3f3f3f3f;
            if (l<=L and R<=r)
            {
                return S;
            }
            PushDown(index);
            return min(Query(l,r,ls),Query(l,r,rs));
        }
}Tree;
void Input_Solve()
{
