using namespace std;
int m,n;
int minn[100001][22];
int RMQ_init()
{
    for(int j=1;1<<j<=m;j++)
        for(int i=1;i+(1<<j)-1<=m;i++)
            minn[i][j]=min(minn[i][j-1],minn[i+(1<<(j-1))][j-1]); 
}
int RMQ_ask(int l,int r){
