    n,m,i,j,x,y:longint;
function min(a,b:longint):longint;
begin
  if a<b then exit(a);
  exit(b);
end;
procedure init;
var i:longint;
begin
  readln(n,m);
  for i:=1 to n do
   read(f[i,0]);
end;
procedure pre;//预处理RMQ
var i,j:longint;
begin
  for j:=1 to 20 do
   for i:=1 to n do
   begin
     if i+jc[j]-1>n then  break;//别忘了-1
     f[i,j]:=min(f[i,j-1],f[i+jc[j-1],j-1]);//同上
   end;
end;
function find(l,r:longint):longint;//查找
var k:longint;
begin
  k:=trunc(ln(r-l+1)/ln(2));//关键
  exit(min(f[l,k],f[r-jc[k]+1,k]));
end;
begin
  init;
  pre;
  for i:=1 to m do
  begin
    readln(x,y);
