思路：这道题明显线段树。设t[x].l以及t[x].r分别为此节点的l—r区间的父节点，t[x].w为其子节点的最小值，然后就是线段树了。。

```cpp

#include<iostream>
#include<cstdlib>
#include<math.h>
#include<stdio.h>
#include<algorithm>
#include<cstring>
using namespace std;
struct tree
{
    int l,r,w;
}t[2000001];
int n,m;
void init(int x,int l,int r)
{
    if(l==r)
    {
      t[x].l=l;
      t[x].r=r;
      t[x].w=0x3f3f3f3f;
      return;
    }
    t[x].l=l;
    t[x].r=r;
    t[x].w=0x3f3f3f3f;
    int mid=(l+r)>>1;
    init(x*2,l,mid);
    init(x*2|1,mid+1,r);
}
int add(int x,int l,int r,int s,int a)
{
    if(l==r&&l==s)
    {
      t[x].w=a;
      return a;
    }
    if(l==r)
      return t[x].w;
    int mid=(l+r)>>1;
    if(mid+1<=s)
      add(x*2|1,mid+1,r,s,a);
    else add(x*2,l,mid,s,a);
    t[x].w=min(t[x*2].w,t[x*2|1].w);
    return t[x].w;
}
int mi=0x3f3f3f3f;
void ask(int x,int l,int r,int a,int b)
{
    if(l>=a&&r<=b)
    {
      mi=min(t[x].w,mi);
      return;
    }
    if(l==r)
      return;
    int mid=(l+r)>>1;
    if(mid>=a)
    ask(x*2,l,mid,a,b);
    if(mid+1<=b)
    ask(x*2|1,mid+1,r,a,b);
    return;
}
int main()
{
    scanf("%d%d",&n,&m);
    init(1,1,n);    
    int i;
    for(i=1;i<=n;i++)
    {
      int j;
      scanf("%d",&j);
      add(1,1,n,i,j);
    }
    int j;
    for(i=1;i<=m;i++)
    {
      int a,b;
      scanf("%d%d",&a,&b);
      ask(1,1,n,a,b);
      printf("%d ",mi);
      mi=0x3f3f3f3f;
    }
    return 0;
}

