bool vis[1005][1005];
int dx[4]={0,1,0,-1};
int dy[4]={1,0,-1,0};
int ans;
void dfs(int x,int y,int n,int m){
	if(x>=n||y>=m||y<1||x<1){
		ans++;
		return;
	}
	vis[x][y]=1;
	for(int i=0;i<4;i++){
		int nx=x+dx[i],ny=y+dy[i];
		if(!vis[nx][ny]){
			dfs(nx,ny,n,m);
		}

	}
	vis[x][y]=0;
}
signed main(){
	//cin >> n >> m;
	for(int n=1;n<=7;n++) for(int m=1;m<=8;m++){
		ans=0;
		for(int i=1;i<n;i++){
			memset(vis,0,sizeof vis);
			vis[i][0]=1;
			dfs(i,1,n,m);
		}
		for(int i=1;i<m;i++){
			memset(vis,0,sizeof vis);
			vis[0][i]=1;
			dfs(1,i,n,m);
		}
		printf("if(n==%d&&m==%d) cout<<%lld<<endl;\n",n,m,ans*2);
	}
	return 0;
}

