#include<cstdio>
using namespace std;
int v[10][10],n,m,dx[5]={0,0,1,-1},dy[5]={1,-1,0,0};
long long ans;
void dfs(int x,int y){
	if(x<1||x>=n||y<1||y>=m){
		ans++;
		return;
	}
	v[x][y]=1;
	for(int i=0;i<=3;i++){
		if(!v[x+dx[i]][y+dy[i]]){
			dfs(x+dx[i],y+dy[i]);
		}
	}
	v[x][y]=0;
}
int main(){
	scanf("%d%d",&n,&m);
	for(int i=1;i<m;i++){
		v[0][i-1]=0;
		v[0][i]=1;
		dfs(1,i);
	}
	v[0][m-1]=0;
	for(int i=1;i<n;i++){
        v[i-1][0]=0;
		v[i][0]=1;
		dfs(i,1);
	}
	printf("%lld",ans*2);
	return 0;
}
