## part 1

```cpp
#include <bits/stdc++.h>
#define int long long
#define re register

using namespace std;

const int N = 10;
int n,m,ans;
int dx[] = {0,0,0,1,-1};
int dy[] = {0,1,-1,0,0};
bool vis[N][N];

inline int read(){
	int r = 0,w = 1;
	char c = getchar();
	while (c < '0' || c > '9'){
		if (c == '-') w = -1;
		c = getchar();
	}
	while (c >= '0' && c <= '9'){
		r = (r << 3) + (r << 1) + (c ^ 48);
		c = getchar();
	}
	return r * w;
}

inline void dfs(int x,int y){
	if (x < 1 || x >= n || y < 1 || y >= m){
		ans++;
		return;
	}
	for (re int i = 1;i <= 4;i++){
		int tx = x + dx[i];
		int ty = y + dy[i];
		if (!vis[tx][ty]){
			vis[tx][ty] = true;
			dfs(tx,ty);
			vis[tx][ty] = false;
		}
	}
}

signed main(){
	n = read();
	m = read();
	for (re int i = 1;i < n;i++){
		memset(vis,false,sizeof(vis));
		vis[i][0] = vis[i][1] = true;
		dfs(i,1);
	}
	for (re int i = 1;i < m;i++){
		memset(vis,false,sizeof(vis));
		vis[0][i] = vis[1][i] = true;
		dfs(1,i);
	}
	printf("%lld",ans << 1);
	return 0;
}
