#include<bits/stdc++.h>
using namespace std;
int a[100010], n, m;
//valid(x)为差值为x是否可行
bool valid(int x) {
	int cnt = 0, last = -(1 << 30); //-INF，保证第一个被选进去
	for (register int i = 1; i <= n; ++i)
		if (a[i] - last >= x) ++cnt, last = a[i];
	//由于我们排好了序，所以比较两个决策a[i]和a[i+1],a[i+1]更容易满足条件
	return cnt >= m;
}
