	cin >> n >> m;
	for (register int i = 1; i <= n; ++i) scanf("%d", a + i);
	sort(a + 1, a + n + 1);//快速排序，方便判定
	int l = 0, r = 1e9;
	//始终在区间l-r进行二分
	//单调性：对于可行的决策mid，比mid小的决策依然可行 
	while (l < r) {
		int mid = (l + r + 1) >> 1;
		if (valid(mid)) l = mid;//如果mid可行，区间缩小为mid-r范围内
		else r = mid - 1;//否则，向更容易成功的地方寻找答案
	}
	cout << l;
	//O(nlog1e9)
}
