```
#include<bits/stdc++.h>
#pragma GCC optimize(2)//手动O2优化
using namespace std;
const int N=1E5+4;//const定义的值不会改变哦
int n,m,x[N];
inline bool check(int d)//bool型函数返回值只有1和0两种哦
{
	int cow=1;
	int rgt=x[1]+d;
	for(register int i=2;i<=n;++i)
	{
		if(x[i]<rgt) continue;
		cow++;
		rgt=x[i]+d;
	}
	return cow>=m;

}

int main () //主函数开始
{
