        cin>>x1>>y1>>x2>>y2;
        bomb[i].setPoint(x1,y1);
        bomb[i+1].setPoint(x2,y2);
    }
    vector<Point> key(y);
    for(int j=0;j<y;j++){
        int x0,y0;
        cin>>x0>>y0;
        key[j].setPoint(x0,y0);
    }
    for(int k=0;k<y;k++){
        int count=0,lastRound;
