	int k = 0;
	while(x){
		shu[++k] = x%10;
		x/= 10;
	}
	ll ans = 0;
	for(int i=1;i<=k;i++){
		memset(dp,-1,sizeof(dp));
		ans += dfs(k,i,0,1);
	}
	return ans-k+1;
}
