void write(int);
void DPfinite(int, int, int);
// finite：有限的
void DPinfinite(int, int);
// infinite：无限的，我们平时常说的 INF 就是它的缩写哦
int main() {
	Ts.h = read(), Ts.m = read();
	Te.h = read(), Te.m = read();
	m = 60 * (Te.h - Ts.h) + Te.m - Ts.m;
  	// m 指两个时间之间还有多长的时间
	n = read();
	for(int i = 1; i <= n; ++i) {
		int t = read(), c = read(), p = read();
		p ? DPfinite(t, c, p) : DPinfinite(t, c);
        // 使用三目运算符，小小地优化一下常数
        // 若 p 非 0，跑有限次背包
        // 否则，跑完全背包，即无限次背包
	}
	cout << dp[m];
    // 输出最大价值
	return 0;
}
int read() {
	int x = 0, ch = getchar();
	while(!isdigit(ch)) ch = getchar();
	while(isdigit(ch)) x = x * 10 + ch - 48, ch = getchar();
	return x;
} 
void write(int x) {
	if(x > 9) write(x / 10);
	write(x % 10 + 48);
}
// 可爱的 IO 优化
void DPfinite(int t, int c, int p) {
	for(int i = 1; i <= p; ++i)
		for(int j = m; j >= t; --j)
			dp[j] = max(dp[j], dp[j - t] + c);
    // 跑有限次背包（多重+01）
}
void DPinfinite(int t, int c) {
	for(int i = t; i <= m; ++i)
		dp[i] = max(dp[i], dp[i - t] + c);
    // 跑无限次背包（完全）
}
