	int n,m;
	m=(h2*60+m2)-(h1*60+m1);
	scanf("%d",&n);
	int k=0,l=0;
	for(int i=1;i<=n;i++)
	{
		int x,y,z;
		scanf("%d%d%d",&x,&y,&z);
		if(z)
		{
			k++;
			w1[k]=x;
			c1[k]=y;
			s1[k]=z;
		}
		else
		{
			l++;
			f2[l].w=x;
			f2[l].c=y;
		}
	}
	//第二步
	if(l==1)
	{
		k++;
		w1[k]=f2[l].w;
		c1[k]=f2[l].c;
		s1[k]=m/w1[k];
	}
	else
	{
		sort(f2+1,f2+1+l,com);
		for(int i=1;i<=l-1;i++)
		{
			bool q=0;
			for(int j=i+1;j<=l;j++)
			{
				if(f2[i].w>f2[j].w&&f2[i].c<f2[j].c)
				{
					q=1;
					break;
				}
			}
			if(!q)
			{
				k++;
				w1[k]=f2[i].w;
				c1[k]=f2[i].c;
				s1[k]=m/w1[k];
			}
		}
		k++;
		w1[k]=f2[l].w;
		c1[k]=f2[l].c;
		s1[k]=m/w1[k];
	}
	//第三步
	int p=0;
	for(int i=1;i<=k;i++)
	{
		for(int j=1;s1[i]>0;j<<=1)
		{
			int d=min(j,s1[i]);
			p++;
			wf[p]=d*w1[i];
			cf[p]=d*c1[i];
			s1[i]-=j;
		}
	}
	//第四步
	for(int i=1;i<=p;i++)
	{
		for(int j=m;j>=wf[i];j--)
		{
			f[j]=max(f[j],f[j-wf[i]]+cf[i]);
		}
	}
	printf("%d\n",f[m]);
	return 0;//完结撒花，此时0:13
}
