#include<bits/stdc++.h>
using namespace std;
int bag,w[1001],v[1001],n[1001],f[1001],a;
void bagg(int w,int v){
	int i;
	for(i=bag;i>=w;i--){
		f[i]=max(f[i],f[i-w]+v);
	}
}//01背包 
void bagcomplete(int w,int v){
	int i;
	for(i=w;i<=bag;i++){
		f[i]=max(f[i],f[i-w]+v);
	}
}//完全背包 
void bagmultip(int w,int v,int n){
	if(bag<=n*w){//如果总容量比这个物品的容量要小，那么这个物品可以直到取完，相当于完全背包

		bagcomplete(w,v);
		return;
	}
	else{//否则就将多重背包转化为01背包
		int k=1;
		while(k<=n){
			bagg(k*w,k*v);
			n-=k;
			k*=2;
		}
	}//增加了二进制优化 
	bagg(n*w,n*v);
}//多重背包 
