              if (not x[a[i,3]])and(a[i,2]=j) then
                begin
                  x[a[i,3]]:=true;
                  inc(m);
                end;
              if m=3 then
                       begin
                         write('xiaoa wins.');
                         exit;
                       end;
            fillchar(x,sizeof(x),false);
            m:=0;
            for i:= 1 to 4 do
              if (not x[b[i,3]])and(b[i,2]=j) then
