#include<queue>
#include<cstdio>
using namespace std;
const int N=1002; //多开一点防越界 
int n,i,j,k,xr[4]={-1,0,1,0},yr[4]={0,1,0,-1}; //四个方向数组(按顺序上下左右) 
char c[N][N];
bool f[N][N];
struct dot{int x,y,num;};  //每个点上的坐标和填充的数字
priority_queue<int,vector<int>,greater<int> >ans;  //用优先队列来做最后的sort 
int main()
{
    scanf("%d",&n);
    for (i=1;i<=n;i++)
        for (j=1;j<=n;j++)
            scanf(" %c",&c[i][j]),
            c[i][j]-=48;  //转换为01比较方便 
    for (j=1;j<=n;j++)
        for (i=1;i<=n;i++)
            if (c[i][j]&&!f[i][j])
            {
                int nowans=1;
                queue<dot>q;
                q.push((dot){i,j,1});
                while (q.size())
                {
                    int x=q.front().x,
                        y=q.front().y,
                        pos=q.front().num+1;
                    q.pop();f[x][y]=1;
                    for (k=0;k<4;k++)
                    {
                        int a=x+xr[k],b=y+yr[k];
                        if (c[a][b]&&!f[a][b])
                            q.push((dot){a,b,pos}),nowans=pos,
                            f[a][b]=1;
                    }
                }
                ans.push(nowans);
            }
    printf("%d\n",ans.size());
    while (ans.size())
        printf("%d\n",ans.top()),ans.pop();
