#include <stdio.h>

int main(void)
{
	long map[2001][4];
	long x1,y1,x2,y2;
	int N,M;
	int i,j,count,num;

	scanf("%d %d", &N,&M);
	for(i = 0; i < N; i++)
	{
		scanf("%ld %ld %ld %ld", &x1,&y1,&x2,&y2);
		map[i][0] = x1;
		map[i][1] = y1;
		map[i][2] = x2;
		map[i][3] = y2;
	}
	for(i = 1; i <= M; i++)
	{
		count = 0;
		scanf("%ld %ld", &x1,&y1);
		for(j = 0; j < N; j++)
		{
			if((x1>=map[j][0]&&x1<=map[j][2]) && (y1>=map[j][1]&&y1<=map[j][3])) //左上角到右下角
            {
                count++;
                num = j;
            }
		}
		if(count)
		{
			printf("YES %d %d", count,num+1);
		}
		else
		{
			printf("NO");
		}
		printf("\n");
	}

	return 0; 
} 
