#include <iostream>
using namespace std;
long long a[2005][4],x,y;  //a数组每轮轰炸影响范围。x、y为各关键点位置。 
int main()
{
	int m,n,cnt,k;  //cnt统计被炸次数， k记录最后被炸轮次。 
	cin>>m>>n;     //轰炸轮数和关键点数量 
	for(int i=1;i<=m;i++)
	    cin>>a[i][0]>>a[i][1]>>a[i][2]>>a[i][3];  //每轮轰炸威力范围 
	    //下面这个统计各关键点被炸情况 
	for(int j=1;j<=n;j++)
	{
		cin>>x>>y;   //关键点位置 
		cnt=0;     //还没被炸 
		for(int i=1;i<=m;i++)
		{
			if((x>=a[i][0]&&x<=a[i][2])&(y>=a[i][1]&&y<=a[i][3]))
			{
				cnt++;    //挨炸一次 
				k=i;      //轮次更新 
			}
		}
		if(cnt==0)   //k为0说明平安无事 
		    cout<<"NO"<<endl;
		else   //k不为0，说明杯具了，情况汇报 
