int  cx [ N ] , dx [ N ] , c [ N ] [ N ] ;
int  n , m , vv , ee , cn , x , y , w ;
bool  vis [ N ] ;

double  minx ( double  a , double  b ) {
    return  a > b ? b : a ;
}

int  minxint ( int  a , int  b ) {
    return  a > b ? b : a ;
}

int  main ( ) {
    //freopen ( "classroom.in" , "r" , stdin ) ;
    //freopen ( "classroom.out" , "w" , stdout ) ;
    scanf ( "%d%d%d%d" , & n , & m , & vv , & ee ) ;

    for ( int  i = 1 ; i <= n ; i ++ ) 
        scanf ( "%d" , & cx [ i ] ) ;
    for ( int  i = 1 ; i <= n ; i ++ )
        scanf ( "%d" , & dx [ i ] ) ;

    for ( int  i = 1 ; i <= n ; i ++ )
        scanf ( "%lf" , & p [ i ] ) ;

    for ( int  i = 1 ; i <= vv ; i ++ )
        for ( int  j = 1 ; j <= vv ; j ++ )
            c [ i ] [ j ] = c [ j ][ i ] = inf ;

    for ( int  i = 1 ; i <= ee ; i ++ ) {
        scanf ( "%d%d%d" , & x , & y , & w ) ;
        if( x != y ) {
            c [ x ] [ y ] = c [ y ] [ x ] = minxint ( c [ x ] [ y ] , w ) ;
        }
    }

    for ( int  i = 1 ; i <= vv ; i ++ )  c [ i ] [ i ] = 0 ;

    for ( int  k = 1 ; k <= vv ; k ++ )
        for ( int  i = 1 ; i <= vv ; i ++ )
            for ( int  j = 1 ; j <= vv ; j ++ )
                c [ i ] [ j ] = minxint ( c [ i ] [ j ] , c [ i ] [ k ] + c [ k ] [ j ] ) ;

    for ( int  i = 1 ; i <= n ; i ++ ) 
        for ( int  j = 0 ; j <= m ; j ++ ) 
            dp [ i ] [ j ] [ 0 ] = dp [ i ] [ j ] [ 1 ] = inf ; 
    dp [ 1 ] [ 0 ] [ 0 ] = 0 ; dp [ 1 ] [ 1 ] [ 1 ] = 0 ; 
    for ( int  i = 2 ; i <= n ; i ++ ) {
        for ( int  j = 0 ; j <= m ; j ++ ) {

            dp [ i ] [ j ] [ 0 ] = minx ( 

                                          dp [ i - 1 ] [ j ] [ 0 ] + c [ cx [ i - 1 ] ] [ cx [ i ] ] 
                                          ,
                                          dp [ i - 1 ] [ j ] [ 1 ] + p [ i - 1 ] * c [ dx [ i - 1 ] ] [ cx [ i ] ]
                                          + ( 1 - p [ i - 1 ] ) * c [ cx [ i - 1 ] ] [ cx [ i ] ] 

                                        ) ;
            if ( j ) {
                dp [ i ] [ j ] [ 1 ] = minx ( 

                                              dp [ i - 1 ] [ j - 1 ] [ 0 ] + p [ i ] * c [ cx [ i - 1 ] ] [ dx [ i ] ]
                                              + ( 1 - p [ i ] ) * c [ cx [ i - 1 ] ] [ cx [ i ] ] 
                                              ,
                                              dp [ i - 1 ] [ j - 1 ] [ 1 ] 
                                              + ( 1 - p [ i - 1 ] ) * ( 1 - p [ i ] ) * c [ cx [ i - 1 ] ] [ cx [ i ] ]
                                              + ( 1 - p [ i - 1 ] ) * p [ i ] * c [ cx [ i - 1 ] ] [ dx [ i ] ] 
                                              + p [ i - 1 ] * ( 1 - p [ i ] ) * c [ dx [ i - 1 ] ] [ cx [ i ] ] 
                                              + p [ i - 1 ] * p [ i ] * c [ dx [ i - 1 ] ] [ dx [ i ] ] 

                                            ) ;
            }
        } 
    }
    double  ans = inf ;
    for ( int  i = 0 ; i <= m ; i ++ )
        ans = minx ( ans , minx ( dp [ n ] [ i ] [ 0 ] , dp [ n ] [ i ] [ 1 ] ) ) ;
