```cpp
#include <iostream>
#include <cstring>
#include <cstdio>
#include <queue>
using namespace std;
struct data{
    int v,w,nxt;
}edge[180010];
const int INF=0x3f3f3f3f;
int n,m,v,e,p,head[310],c[2010],d[2010],dis[310][310];
double ans=INF,k[2010],f[2010][2];
bool inq[310];
queue<int> q;
template <typename Tp> inline void read(Tp &x)
{
    x=0;
    char ch=getchar();
    while(ch<'0'||ch>'9') ch=getchar();
    while(ch>='0'&&ch<='9') x=x*10+ch-'0',ch=getchar();
}
inline double fmin(double x,double y){return x<y?x:y;}
inline void insert(int u,int v,int w)
{
    edge[++p].v=v;edge[p].w=w;
    edge[p].nxt=head[u];head[u]=p;
    edge[++p].v=u;edge[p].w=w;
    edge[p].nxt=head[v];head[v]=p;
}
void input()
{
    read(n),read(m),read(v),read(e);
    int u,v,w;
    for(int i=1;i<=n;i++)
      read(c[i]);
    for(int i=1;i<=n;i++)
      read(d[i]);
    for(int i=1;i<=n;i++)
      scanf("%lf",&k[i]);
    for(int i=1;i<=e;i++)
    {
        read(u),read(v),read(w);
        if(u==v)
          continue;
        insert(u,v,w);
    }
}
void spfa(int u)
{
    int x,y;
    memset(inq,0,sizeof(inq));
    for(int i=1;i<=v;i++)
      dis[u][i]=INF;
    dis[u][u]=0;
    while(!q.empty())
      q.pop();
    q.push(u);
    inq[u]=true;
    while(!q.empty())
    {
        x=q.front();
        q.pop();
        inq[x]=false;
        for(int i=head[x];i;i=edge[i].nxt)
        {
            y=edge[i].v;
            if(dis[u][y]>dis[u][x]+edge[i].w)
            {
                dis[u][y]=dis[u][x]+edge[i].w;
                if(!inq[y])
                  q.push(y),inq[y]=true;
            }
        }
    }
}
int main()
{
    int cc,cd,dc,dd;
    double t;
    input();
    for(int i=1;i<=v;i++)
      spfa(i);
    for(int j=0;j<=m;j++)
      f[j][0]=f[j][1]=INF;
    f[0][0]=f[1][1]=0.0;
    for(int i=2;i<=n;i++)
    {
        cc=dis[c[i-1]][c[i]];cd=dis[c[i-1]][d[i]];
          dc=dis[d[i-1]][c[i]];dd=dis[d[i-1]][d[i]];
          for(int j=(i<m?i:m);j>=1;j--)
        {
              t=fmin(f[j][0]+cc,f[j][1]+k[i-1]*dc+(1.0-k[i-1])*cc);//上次不换/换了
            f[j][1]=fmin(f[j-1][0]+k[i]*cd+(1.0-k[i])*cc,f[j-1][1]+dd*k[i-1]*k[i]+cd*(1.0-k[i-1])*k[i]+dc*k[i-1]*(1.0-k[i])+cc*(1.0-k[i-1])*(1.0-k[i]));//上次不换/换了
            f[j][0]=t;
        }
        f[0][0]+=cc;
    }
    for(int j=0;j<=m;j++)
    {
        if(ans>f[j][0])
          ans=f[j][0];
        if(ans>f[j][1])
          ans=f[j][1];
    }
    printf("%.2lf\n",ans);
    return 0;
}
