#include<cstdio>
#include<algorithm>
using namespace std;
double k[2005],a[305][305];
double f[2005][2005][2];
int c[2005],d[2005];
int main()
{
	int n,m,v,e;
	scanf("%d%d%d%d",&n,&m,&v,&e);
	for(int i=1;i<=n;i++)
	{
		scanf("%d",&c[i]);
	}
	for(int i=1;i<=n;i++)
	{
		scanf("%d",&d[i]);
	}
	for(int i=1;i<=n;i++)
	{
		scanf("%lf",&k[i]);
	}
	for(int i=1;i<=301;i++)
	{
		for(int j=1;j<=301;j++)
		{
			a[i][j]=1000000000;
		}
	} 
	for(int i=1;i<=300;i++)
	a[i][i]=0; 
	for(int i=1;i<=e;i++)
	{
		int x,y;
		double z;
		scanf("%d%d%lf",&x,&y,&z);
		a[x][y]=a[y][x]=min(a[x][y],z);
	}

	for(int kk=1;kk<=v;kk++)
	{
		for(int i=1;i<=v;i++)
