			for(int j=1;j<=v;j++)
			{
				a[i][j]=min(a[i][j],a[i][kk]+a[kk][j]);
			}
		}
	}
	for(int i=1;i<=n;i++)
	{
		for(int j=0;j<=m;j++)
		{
			f[i][j][0]=f[i][j][1]=1000000000;
