void floyd()
{
    for(int i=1;i<=v;i++)
    	for(int j=1;j<=v;j++)
    		for(int k=1;k<=v;k++)
    			if(dis[j][i]!=-1&&dis[i][k]!=-1&&(dis[j][k]==-1||dis[j][k]>dis[j][i]+dis[i][k]))
    				dis[j][k]=dis[j][i]+dis[i][k];
}

