using namespace std;

const int inf = 5e8;
const int N = 2001;
const int V = 301;
int n,m,v,e;
int c[N],d[N];
int dis[V][V];
double k[N];
double f[N][N][2];
double ans=inf;

inline void readx(int &x)
{
	x=0; int s=-1; char ch=getchar();
	while(ch<'0'||ch>'9') {if(ch=='-') s=-1;ch=getchar();}
	while(ch>='0'&&ch<='9') {x=(x<<1)+(x<<3)+ch-'0';ch=getchar();}
	x*=s;
}

inline void Init()
{
	scanf("%d%d%d%d",&n,&m,&v,&e);
	for(int i=1;i<=v;++i) for(int j=1;j<=v;++j) dis[i][j]=inf;
	for(int i=1;i<=n;++i) scanf("%d",&c[i]);
	for(int i=1;i<=n;++i) scanf("%d",&d[i]);
	for(int i=1;i<=n;++i) scanf("%lf",&k[i]);
	for(int i=1;i<=e;++i)
	{int _a,_b,_c;scanf("%d%d%d",&_a,&_b,&_c);dis[_a][_b]=dis[_b][_a]=min(dis[_a][_b],_c);}
//这里千万记得判重边，这个重边贼恶心人！！！ 

	for(int i=1;i<=v;++i) dis[i][i]=0;
	for(int k=1;k<=v;++k)
		for(int i=1;i<=v;++i)
			for(int j=1;j<=v;++j)
				dis[i][j]=min(dis[i][j],dis[i][k]+dis[k][j]);

	for(int i=1;i<=n;++i)
		for(int j=0;j<=m;++j)
			f[i][j][0]=f[i][j][1]=inf;
	f[1][0][0]=f[1][1][1]=0;
}
//理解一下应该能看懂 

inline void DP()
{
	for(int i=2;i<=n;++i)
	{
		int mi=min(m,i);
		f[i][0][0]=f[i-1][0][0]+dis[c[i-1]][c[i]];
		for(int j=1;j<=mi;++j)
		{
			int C1=c[i-1],C2=d[i-1],C3=c[i],C4=d[i];
			f[i][j][0]=min(
				f[i-1][j][0]+dis[C1][C3],//C1->C3 
				f[i-1][j][1]+dis[C1][C3]*(1-k[i-1])+dis[C2][C3]*k[i-1]//C1->C3 & C2->C3 
			);

			f[i][j][1]=min(
				f[i-1][j-1][0]+dis[C1][C3]*(1-k[i])+dis[C1][C4]*k[i],//C1->C3 & C1->C4 
				f[i-1][j-1][1]+
					dis[C1][C3]*(1-k[i-1])*(1-k[i])//C1->C3 
					+dis[C1][C4]*(1-k[i-1])*k[i]//C1->C4 
					+dis[C2][C3]*k[i-1]*(1-k[i])//C2->C3 
					+dis[C2][C4]*k[i-1]*k[i]//C2-C4 
			);
//形象直观QAQ 
		}
	}
}
//懒得打方程了(逃 

