int read()
{
    int x=0,w=1;
    char c=getchar();
    while(c<'0'||c>'9'){if(c=='-')w=-1;c=getchar();}
    while(c>='0'&&c<='9'){x=(x<<3)+(x<<1)+c-48;c=getchar();}
    return x*w;
}
void Print(int num,int x)
{
    if(!num) return ;
    Print(num-1,pre[num][x]);
    printf("%d ",x);
}
int main()
{
    int F=read(),V=read();
    for(int i=1;i<=F;i++)
        for(int j=1;j<=V;j++)
            a[i][j]=read();
    for(int i=1;i<=F;i++)
        for(int j=0;j<=V;j++)
            f[i][j]=-1<<30;
    for(int i=1;i<=V-F+1;i++) f[1][i]=a[1][i];
    for(int i=2;i<=F;i++)
        for(int j=i;j<=V-F+i;j++)
        {
            int t=0;
            for(int k=i-1;k<j;k++)
                if(f[i-1][t]<f[i-1][k])
                    t=k;
            f[i][j]=f[i-1][t]+a[i][j];
            pre[i][j]=t;
