int v[105][105],f[105][105];
int pre[105][105],p[105];
int n,m,ans;
int main()
{
    scanf("%d%d",&n,&m);//因为f,v重名了就继续用n,m了 
    for(int i=1;i<=n;++i)
     for(int j=1;j<=m;++j)
      scanf("%d",&v[i][j]);

    for(int i=1;i<=n;++i)
     for(int j=1;j<=m;++j)
      f[i][j]=-1000000;//init

    for(int i=1;i<=n;++i)
     for(int j=i;j<=m-n+i;++j)//因为后面还有花要放所以时(i，m-n+i)
      for(int k=i-1;k<j;++k)
       if(f[i][j]<f[i-1][k]+v[i][j])
        f[i][j]=f[i-1][k]+v[i][j],pre[i][j]=k;//只要更新那么目前上一层最佳位置就是k 

    for(int i=n;i<=m;++i)
     if(ans<f[n][i])
      ans=f[n][i],p[n]=i;
      //最后一个的位置已经找到 
    printf("%d\n",ans);
    for(int i=n;pre[i][p[i]];i--)
     p[i-1]=pre[i][p[i]];
     //通过下一层的pre找到该层位置,不存在的时候就停止 
    for(int i=1;i<=n;++i)
