int a[109][109],fmax[109][109],c[109][109],d[101],f,v,max=0,k;
int main()
{
    scanf("%d%d",&f,&v);
    for(int i=1;i<=f;i++)
    {
        for(int j=1;j<=v;j++)
        {
            scanf("%d",&a[i][j]);
        }
    }
    for(int i=1;i<=v-f+1;i++){fmax[1][i]=a[1][i];}//初始化
    for(int i=2;i<=f;i++)
    {
        for(int j=i;j<=v-f+i;j++)
        {
            for(int k=i-1;k<j;k++)
            {
                if(fmax[i-1][k]+a[i][j]>fmax[i][j])
                {
                    fmax[i][j]=fmax[i-1][k]+a[i][j],//状态转移
                    c[i][j]=k;//记录路径
                }
            }
        }
    }
    for(int i=f;i<=v;i++)
    {
        if(fmax[f][i]>max)
        {
            max=fmax[f][i];//找答案
            k=i;//找路
        }
