    sort(a+1,a+n+1,cmp2);
    for (int i=1;i<=n;i++) a[i].z+=a[i-1].z;//前缀和 
    memset(f,0x7f,sizeof f);
    for (int i=1;i<=n+1;i++)
     for (int w=0;w<=n;w++)
