}data[100];
struct node1{
	long long id,a;
}b[100];
long long f[200][200][200],sum[200];
long long read()
{
	long long ret(0);
	char ch=getchar();
	while (ch<'0'||ch>'9') ch=getchar();
	while (ch>='0'&&ch<='9')
	{
		ret=(ret<<1)+(ret<<3)+ch-48;
		ch=getchar();
	}
	return ret;
}
bool cmp(node x,node y)
{
	return x.a<=y.a;
}
bool cmp1(node1 x,node1 y)
{
	return x.a<=y.a;
}
int main()
{
