        if(r%2==1)
            res=(res*a)%p;
        r/=2;
        a=a*a%p;
    }
    return res;
}
ll lucas(ll n,ll k,ll p)//C(n,k)%p
{
    ll res=1;
    while(n&&k)
    {
        ll nn=n%p;
        ll kk=k%p;
        if(nn<kk)
            return 0;//inv (f[kk])=f[kk]^(p-2)%p
        res=res*f[nn]*poww(f[kk]*f[nn-kk]%p,p-2,p)%p;
        n/=p,k/=p;
    }
    return res;
}
int main()
{
    int t,n,k;
    init(2);//init(p)
    cin>>t;
    while(t--)
    {
        cin>>n>>k;
        cout<<lucas(n,k,2)<<endl;
