#include<bits/stdc++.h>
using namespace std;
#define N 1000002
long long int n,m,sum;
long long int a[N];
int main()
{
	cin>>n>>m;
	for(long long int i=1;i<=n;i++)
	{
		cin>>a[i];
	}
	sort(a+1,a+n+1);
	a[n+1]=a[n];
	for(long long int i=n;i>=0;i--)
	{
		sum+=(n-i)*(a[i+1]-a[i]);
		if(sum<m)
		{
			continue;
		}
		long long int before=sum-(n-i)*(a[i+1]-a[i]);
		long long int cha=m-before;
		long long int ans=a[i+1]-(cha/(n-i));
		if(cha%(n-i)!=0)
		{
			ans--;
		}

		cout<<ans;
		break;
	}
	return 0;
}
