int a[10000005];
int n, m;
bool check(int x)//判断当高度为x米时，是否大于要求高度 
{
	long long sum = 0;//因为每棵树的高度相加可能超过int 
	for(int i = 1; i <= n; i++)
	{
		if(a[i] > x)
		{
			sum = sum + a[i] - x;//累加 
		}
	}
	return sum >= m;//如果sum>=m，返回true，否则返回false; 
}
int main()
{
	int l = 1, r;//最高最低边界 
	cin >> n >> m;
	for(int i = 1; i <= n; i++)
	{
		cin >> a[i];//输入高度 
		r = max(r, a[i]);//记录最高边界 
	}
