#include <iostream>
#include <cstring>
#include <cstdio>
#include <cmath>
#include <algorithm>
#define ll long long 
using namespace std;
ll n,m;
ll tre[1000100];
bool cmp(ll a,ll b){return a>b;} //降序
int main()
{
	cin>>n>>m;
	for(int i=1;i<=n;i++)
		cin>>tre[i];
	sort(tre+1,tre+n+1,cmp); 
	ll lon=0；//lon：当前得到的木材长度
	ll tot=0;//tot：当前可以砍到的树的数量
	ll ans=tre[1],i=1;//ans：锯片高度
	while(lon<m&&i<=n)
	{
	    ans--;
		while(tre[i]>ans) 
		{
		    i++;
		    tot++;
		}
		lon+=tot;
		if(lon>=m) break;
	}
	cout<<ans;
	return 0;	
}
