    cin>>n>>be>>maxz;
    for(int i=1;i<=n;i++){cin>>a[i];}
    dp[1]=be;tong[be]=1;
    for(int i=1;i<=n;i++){
    int tot=0,truee=0;
      for(int j=0;j<=maxz;j++){
       if(tong[j]==0);
       else{
       truee+=2;
       dp[tot]=j+a[i];
       dp[tot+1]=j-a[i];
       if(dp[tot]<0)truee--;
       if(dp[tot+1]>maxz)truee--;
       tot+=2;
       }
                      }
       if(truee==0)
       {cout<<"-1";
       return 0;
       }
       GSH();
       for(int j=0;j<tot;j++){
         if(dp[j]<0)continue;
         tong[dp[j]]=1;
       }
    }
    ans=-1;
    for(int i=0;i<=maxz;i++){
     if(tong[i])ans=i;
    }
    cout<<ans;
    return 0;
