using namespace std;
#define MAXN 100
#define MAXV 2000
int c[MAXN+5], n, S, X;
int f[MAXN+5][MAXV+5];
int Robot(int p, int v)
{
    if(p == n+1)
        return v;
    if(f[p][v]+1)
        return f[p][v];
    int ans = -1;
    if(v-c[p] >= 0)
        ans = max(ans,Robot(p+1,v-c[p]));
    if(v+c[p] <= X)
        ans = max(ans,Robot(p+1,v+c[p]));
    return (f[p][v]=ans);
}
int main()
{
    scanf("%d%d%d",&n,&S,&X);
    for(int i = 1; i <= n; i++)
        for(int v = 0; v <= X; v++)
            f[i][v] = -1;
    for(int i = 1; i <= n; i++)
        scanf("%d",c+i);
    printf("%d\n",Robot(1,S));
    return 0;
}
```

