#include<iostream>
#include<cstring>
#include<cstdio>
#include<algorithm>
#include<cctype>
#define maxn 200
using namespace std;
inline int read(){
	int x=0;bool y=0;char ch;
	while(!isdigit(ch)){
		if(ch=='-')y=1;
		ch=getchar();
	}
	while(isdigit(ch)){
		x=(x<<3)+(x<<1)+(ch^48);
		ch=getchar();
	}
	return y?-x:x;
} 
signed main(){
	int a[maxn<<1],sum[maxn<<1];int n;
	int f1[maxn<<1][maxn<<1],f2[maxn<<1][maxn<<1];
	cin>>n;
	for(int i=1;i<=n;i++){
		a[i]=read();a[i+n]=a[i];}
//	memset(f1,0,sizeof(f1));
	memset(f2,0x3f,sizeof(f2));
	sum[0]=0;
	for(int i=1;i<=2*n;i++){
		f1[i][i]=0;
		f2[i][i]=0;
		sum[i]=sum[i-1]+a[i];
	}
	for(int len=2;len<=n;len++){
		for(int l=1;l<=2*n;l++){
			int r=l+len-1;
			if(r>2*n)continue;
			f1[l][r]=0;
			f2[l][r]=100000000;
			for(int k=l;k<r;k++){
				int suma=sum[r]-sum[l-1];
				f1[l][r]=max(f1[l][k]+f1[k+1][r]+suma,f1[l][r]);
				f2[l][r]=min(f2[l][k]+f2[k+1][r]+suma,f2[l][r]);
			}
		}
	}
	int MAX=0,MIN=0x7fffffff;
	for(int i=1;i<=n;i++)
	{
		MAX=max(MAX,f1[i][i+n-1]);
		MIN=min(MIN,f2[i][i+n-1]);
	}
	printf("%d\n",MIN);
	printf("%d\n",MAX);
	return 0;
}
