var l,n,i,j,k,s:longint;
    t:extended;  //因为是0.5长度的枚举，所以必须用实数类型。
    bl:boolean; //后面判断用。
    sh:array[0..100000] of longint; //此处开数组必须要开0，模拟绳子。
begin
