        begin
          k:=trunc(t);
          for j:=0 to k do
          if ((sh[j]=1)and(sh[trunc(t*2)-j]=0))or((sh[j]=0)and(sh[trunc(t*2)-j]=1)) then //判断节点是否一一对应。
