{
    scanf("%d%d",&n,&m);
    one=n/m,sum=one*m;//把n平均分
    for(int i=n-sum+1;i<=m;i++){printf("%d ",one);}//输出没有多的部分
    for(int i=1;i<=n-sum;i++){printf("%d ",one+1);}//多的部分
