#include<iostream>
#include<cstdio>
#include<algorithm>
using namespace std;
int a[5];//储存边
void qsort(int l,int r)
{
    int mid=(l+r)/2,i=l,j=r;
    mid=a[mid];//取中间数，本蒟蒻不会用随机快排= =
    while(i<=j)
    {
        while(a[i]<mid)//在左半部分寻找比中间数大的数
            i++;
        while(a[j]>mid)//在右半部分寻找比中间数小的数
            j--;
        if(i<=j)//保证不越界
        {
            swap(a[i],a[j]);//swap交换两个数
            i++;//继续找
            j--;
        }
    }
    if(l<j)//若没有到两数的边界就递归搜左右区间
        qsort(l,j);
    if(i<r)
        qsort(i,r);
}
int main()
{
    int i,mi,ma;
