        }
    for(int n=1;n<=max;n++)
    {j++; if(min%j==0&&max%j==0&&j>maxn)maxn=j;}
//找最大公约数。j一直增加，如果增加到是两个数的公约数时，就赋值给maxn，直至找出最大公约数，原理与找最大最小数相似。
    cout<<min/maxn<<"/"<<max/maxn;//输出约分之后的结果。
