	 sort(srx,1,n);
	 sort(sry,1,n);//第一遍排序
	 for(i=1;i<=n;i++)
     srx[i]-=i;//之前一直认为是直接在后面减去，结果...
     sort(srx,1,n);//剪完之后就会保证x值不会有重合（如果原先相同由于减去的数不同也会不同）
     if(n%2==0)//求中位数有奇偶之分             
     {x=(srx[n/2]+srx[n/2+1])/2;
     y=(sry[n/2]+sry[n/2+1])/2;}
     else
     {x=srx[n/2+1];
     y=sry[n/2+1];}
     for(i=1;i<=n;i++)
     lhy+=abs(srx[i]-x)+abs(sry[i]-y);//绝对值
	 printf("%d",lhy);
	 return 0;
	 }
