procedure px(l,r:longint;var a:sj);
var
  i,j,k,t:longint;
begin
  i:=l;
  j:=r;
  k:=(l+r) div 2;
  t:=a[k];
  a[k]:=a[l];
  while i<j do
  begin
    while (i<j) and(a[j]>t) do
      dec(j);
    if i<j then
    begin
      a[i]:=a[j];
      inc(i);
    end;
    while (i<j) and(a[i]<t) do
      inc(i);
    if i<j then
    begin
      a[j]:=a[i];
      dec(j);
    end;
  end;
  a[i]:=t;
  if i-1>l then
    px(l,i-1,a);
  if i+1<r then
    px(i+1,r,a);
end;
