//by Judge
#include<cstdio>
#include<cstring>
#include<iostream>
#define Rg register
#define fp(i,a,b) for(Rg int i=(a),I=(b)+1;i<I;++i)
#define fd(i,a,b) for(Rg int i=(a),I=(b)-1;i>I;--i)
#define ll long long
using namespace std;
const int M=1003;
typedef int arr[M];
#ifndef Judge
#define getchar() (p1==p2&&(p2=(p1=buf)+fread(buf,1,1<<21,stdin),p1==p2)?EOF:*p1++)
#endif
char buf[1<<21],*p1=buf,*p2=buf;
inline int read(){ int x=0,f=1; char c=getchar();
	for(;!isdigit(c);c=getchar()) if(c=='-') f=-1;
	for(;isdigit(c);c=getchar()) x=x*10+c-'0'; return x*f;
} char sr[1<<21],z[20];int CCF=-1,Z;
inline void Ot(){fwrite(sr,1,CCF+1,stdout),CCF=-1;}
inline void print(int x,char chr='\n'){
	if(CCF>1<<20)Ot();if(x<0)sr[++CCF]=45,x=-x;
	while(z[++Z]=x%10+48,x/=10);
	while(sr[++CCF]=z[Z],--Z);sr[++CCF]=chr;
} int n,m,a[M],t[M<<2];
#define ls k<<1
#define rs k<<1|1
#define mid ((l+r)>>1)
#define lson ls,l,mid
#define rson rs,mid+1,r
int gcd(int x,int y){return y?gcd(y,x%y):x;}
inline void build(int k,int l,int r){
	if(l==r) return t[k]=a[l],void();
	build(ls,l,mid),build(rs,mid+1,r),t[k]=gcd(t[ls],t[rs]);
}
int query(int k,int l,int r,int L,int R){
	if(l>R || r<L) return 0; if(L<=l && r<=R) return t[k];
	return gcd(query(lson,L,R),query(rson,L,R));
}
int main(){ n=read(),m=read();
	fp(i,1,n) a[i]=read();
	build(1,1,n); Rg int l,r;
	while(m--){ l=read(),r=read();
		print(query(1,1,n,l,r));
	} return Ot(),0;
}
```

