    while(isdigit(ch)){
        v=(v<<1)+(v<<3)+ch-48;
        ch=getchar();
    }
}
inline void putint(int v){
    if(v==0){
        putchar('0');
        return;
    }
    int t=1;
    while(t*10<=v){
        t*=10;
    }
    while(t){
        putchar(v/t+48);
        v%=t;
        t/=10;
    }
}
inline int gcd(int a,int b){
    int r=a%b;
    while(r!=0){
        a=b;
        b=r;
        r=a%b;
    }
    return b;
}
int n,m;
int a[1010],g[1010][1010];                        gcd[i][j]表示数列中第i到j个元素的最大公因素
int l,r;
int main(){
    int i,j;
    getint(n);
    getint(m);
    for(i=1;i<=n;i++){
        getint(a[i]);
    }
    for(i=1;i<=n;i++){
        g[i][i]=a[i];                            一个数的最大公因数就是这个数本身
        for(j=i+1;j<=n;j++){
            if(g[i][j-1]!=1){
                g[i][j]=gcd(g[i][j-1],a[j]);                    计算g[i][j]
            }
        }
    }
    for(i=1;i<=m;i++){
        getint(l);
        getint(r);
        putint(g[l][r]);
        putchar('\n');
