void init(int N=1000000){
    u[1]=1;
    for(int i=2;i<=N;++i){
        if(!vis[i]){
            pr[++pr[0]]=i;
            u[i]=-1;
        }
        for(int j=1;j<=pr[0]&&pr[j]*i<=N;++j){
            vis[i*pr[j]]=1;
            if(!(i%pr[j]))break;
            u[i*pr[j]]=-u[i];
        }
    }
    for(int i=1;i<=N;++i)
        for(int j=1;j*i<=N;++j)
            v[i*j]+=1ll*u[i]*i*j*(j+1)>>1;
    for(int i=1;i<=N;++i)
        for(int j=i;j<=N;j+=i)
            va[j]+=v[j/i];
}
